package org.example;

import org.junit.jupiter.api.Disabled;
import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;

public class LongestPalindromicSubstringTest {
    LongestPalindromicSubstring instance = new LongestPalindromicSubstring();

    @Test
    public void test1(){
        String expectedResult = "bab";
        String actualResult = instance.longestPalindrome("babad");

        assertEquals(expectedResult,actualResult);
    }
    @Test
    public void test2(){
        String expectedResult = "bb";
        String actualResult = instance.longestPalindrome("cbbd");

        assertEquals(expectedResult,actualResult);
    }
    @Test
    public void test3(){
        String expectedResult = "a";
        String actualResult = instance.longestPalindrome("a");

        assertEquals(expectedResult,actualResult);
    }
    @Test
    public void test4(){
        String expectedResult = "a";
        String actualResult = instance.longestPalindrome("ac");

        assertEquals(expectedResult,actualResult);
    }
    @Test
    public void test5(){
        String expectedResult = "bb";
        String actualResult = instance.longestPalindrome("bb");

        assertEquals(expectedResult,actualResult);
    }
    @Test
    public void test6(){
        String expectedResult = "";
        String actualResult = instance.longestPalindrome("");

        assertEquals(expectedResult,actualResult);
    }
    @Test
    public void test7(){
        String expectedResult = "";
        String actualResult = instance.longestPalindrome("");

        assertEquals(expectedResult,actualResult);
    }

    @Test
    @Disabled
    public void test8(){
        String expectedResult = "bb";
        String actualResult = instance.longestPalindrome("bb");

        assertEquals(expectedResult,actualResult);
    }
}
